mapper.select:
SELECT ID_ AS event, PROC_INST_ID_ as id, NAME_ as eventType, ASSIGNEE_ as approvedPerson, PROC_INST_ID_ as taskType, CREATE_TIME_ as createTime FROM act_ru_task
ASSIGNEE_=#{applyPerson}
and ASSIGNEE_=#{approvedPerson}
and REV_ =#{rev}
and NAME_ like CONCAT('%', #{taskType}, '%')

SELECT
count( DISTINCT task.PROC_INST_ID_ )
FROM
act_ru_task task left join act_ru_variable var on var.PROC_INST_ID_=task.PROC_INST_ID_


        task.REV_ =#{rev}


        and to_days(NOW()) - TO_DAYS(task.CREATE_TIME_)    =   0


        and  var.NAME_='approvedPerson' and var.TEXT_=#{approvedPerson}


        and  var.NAME_='applyPerson' and var.TEXT_=#{applyPerson}

SELECT count( DISTINCT task.PROC_INST_ID_ ) FROM act_hi_taskinst task LEFT JOIN act_hi_varinst var ON task.PROC_INST_ID_ = var.PROC_INST_ID_
var.NAME_ = 'approvedPerson' AND var.TEXT_ = #{name}
and to_days(NOW()) - TO_DAYS(task.START_TIME_) = 0
